     console . log ( Event Received :    +   event . type ); 
     console . log ( event . data );          }                 sessionObject . addListener ( onConnection , connectionHandler );  // Adding onConnectionClosed event handler  function   connectionClosedHandler ( event ){ 
     console . log ( Event Received :    +   event . type );       
     console . log ( event . data );          }  sessionObject . addListener ( onConnectionClosed , connectionClosedHandler );  // Adding onError event handler  function   onErrorHandler ( event ){ 
     console . log ( Event Received :    +   event . type );       
     console . log ( event . data );  }  sessionObject . addListener ( onError , onErrorHandler );  //Adding onURLRedirection event handler  function   onURLRedirectionHandler ( event ){ 
     console . log ( Event Received :    +   event . type );       
     console . log ( event . data );  }  sessionObject . addListener ( onURLRedirection , onURLRedirectionHandler );

Example   1   :   To   change   resolution   to   fixed   width   and   height    var   bounds   =   { 
     autoresize : false , 
     width :   800 , 
     height : 600  }  sessionObject . changeResolution ( bounds );    Example   2   :   To   change   the   session   resolution   to   match   the   iframe   element   or   tab   size     var   bounds   =   { 
     autoresize :   true  }  sessionObject . changeResolution ( bounds );

//Removing the event handler for onConnection event  function   connectionHandler ( eventObj ){ 
     console . log ( Event Received :    +   event . type );       
     console . log ( event . data );  }  sessionObject . removeListener ( onConnection , connectionHandler );

